import Book from './book'

const bookList = (props) => {

    handleRemoveClick = (obj) => {
        console.log('REMOVE FUNCTION', obj)
        const removedItemFromBookshelf = this.props.books.filter(book => {
          return <Book handleRemoveClick={props.handleRemoveClick} books={props.books} {...books} key={books.title}/>
        })
        this.setState({
          books: removedItemFromBookshelf
        })
      }

    const laadBoeken = props.books.map( books => { 
            return <Book handleRemoveClick={props.handleRemoveClick} books={props.books} {...books} key={books.title}/>

           })


       return (
        <div >

           <h1>BoekenLijst</h1>

           <ul>{laadBoeken}</ul>



        </div>
    );

};

export default bookList;
