import React from 'react';
import Book from './book'

const bookList = (props) => {
    const laadBoeken = props.books.map( books => { 

            return <div className='ui grid'>
                 <Book key={books.title}  {...books}  />
                 <div>
                 <div className='ui red button label' onClick={() => props.handleDelete(props.id)}>Delete</div>
                 </div>



            </div>



           })

           return (
        <div >
           <h1>BoekenLijst</h1>           
           <ul>{laadBoeken}</ul>                  
        </div>
    );

};

export default bookList;
