import React from 'react';

const book = props => {


    const handleRemoveClick = (obj) => {
        console.log('REMOVE FUNCTION', obj)
        const removedItemFromBookList = this.props.books.filter(book => {
          return book.id !== obj.id
        })
        this.setState({
          books: removedItemFromBookList
        })
      }

       return (
        <div className='ui divided items'> 
            <div className='class="item'>   
                <div className='meta'>     
                    <h2 className="header">{props.title}</h2> 
                    </div>                          
                            <div className='meta' >    
                        <p className= "cinema">{props.auteur}</p>
                            </div>
                        <div className="extra">
                            <p className='ui label'>{props.jaar}</p>
                            <p className='ui label'>{props.price}</p> 
                            <div className='ui label'>
                            Delete     
                            </div>       
                        </div>
                        </div>   
           </div>                      
    );
};

export default book;
