           <form className='ui form'>
               <div className='field'>
                   <label>Titel</label>
                   <input type="text" name="title" placeholder="Title van het boek."></input>
               </div>

               <div className='field'>
                   <label>Auteur</label>
                   <input type="text" name="auteur" placeholder="Naam van auteur"></input>
               </div>

               <div>
                   <label>Jaar</label>
                   <input></input>
               </div>

               <div>
                   <label>Prijs</label>
                   <input></input>
               </div>

               <div>

                   <input type="button" value="Voeg toe"></input>
               </div>
           </form>              
        </div>


    );
    }
};

export default bookForm;
