import React from "react"

import editableHoc from "components/editableHoc"

class EditableText extends React.Component{
    constructor(props){
      super(props)
      console.log(props);
      this.state = {
        name: props.name,
        type: props.type||'text',
        value: props.value||'',
        edit: false
      }
    }

    // onClick = (state, props) => {
