import React from "react";


class EditableText extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            //user input.
            input: "",
            // trigger editable div.
            editModeText: false,
            url: "",
            display: "none",
            iconUrl: "https://kabood-dummy-target.s3.eu-central-1.amazonaws.com/static-v2/img/editable_text_icons/Purple/",
            diapIconUrl: "https://kabood-dummy-target.s3.eu-central-1.amazonaws.com/static-v2/img/editable_text_icons/DIAP/",
            backgroundColor: ""
        };
    }


    componentDidMount() {
        this.setState({backgroundColor: window.getComputedStyle(document.getElementById("contentbox")).backgroundColor})

    }

    componentDidUpdate(prevProps, prevState) {
        if (this.state.editModeText === true) {
            document.getElementById("container-toolbar").style.backgroundColor = this.state.backgroundColor;
            document.getElementById("button-row").style.backgroundColor = this.state.backgroundColor;
            document.getElementById("toolbar").style.backgroundColor = "white";
        }
    }


    render() {
        return (

        <div >
            <h1>Editable Text</h1>
            <p> 
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt 
                ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco 
                laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit 
                in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat 
                cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum
            </p>
        </div>
        );
    }
}

export default EditableText;
