import React from 'react';
import BookList from "./bookList"
import BookForm from "./bookForm"
class App extends React.Component {

    state = {
        books: [{ 

            title: "Harry potter",
            auteur: "J.K. Rowling",
            jaar: 2010,
            price: "45,-"
        },{
            title: "Vermist",
            auteur: "Joe Cuba",
            jaar: 2003,
            price: "20,-"
        },{
            title: "Roadtrip",
            auteur: "Dennis Hopper",
            jaar: 2008,
            price: "15,-"
        },{
            title: "Ugly love",
            auteur: "Janis Joplin",
            jaar: 2016,
            price: "18,-"
        },{
            title: "Rich dad, poor dad",
            auteur: "Harry Mulish",
            jaar: 2022,
            price: "25,-"
        }],
    }

         handleSubmit = (e, obj) => {
          e.preventDefault()
            const submitted = [...this.state.books, obj]
            this.setState({
            books: submitted
            })
            }    

        handleDelete = (bookId) => {
            const newBooks = this.state.books.filter(book => book.id !== bookId);
                this.setState({ books: newBooks });
                console.log("Button Clicked!");
              };

    render () {
        return <div className='ui placeholder segment'>
                <div className= 'ui two column very relaxed stackable grid' >
                    <div className='middle aligned column'>
                        <BookList books={this.state.books}  handleDelete={this.handleDelete}   /> 
                    </div>
                    <div className="middle aligned column">
                         <BookForm handleSubmit={this.handleSubmit}  books={this.state.books} />        
                    </div>
                </div>
        </div>
    }
}

export default App;
