import React from 'react';
import book from './book';
import Book from './book'

const bookList = (props) => {
    const laadBoeken = props.books.map( books => { 

        function handleChange(e) {
            let isChecked = e.target.this.state.books.title;
            console.log(isChecked);
          }



            return <div>

            <input type="checkbox" onChange={e => handleChange(e)} value={book.id}></input>

            <Book key={books.id} handleDelete={props.handleDelete} {...books}  />  
            </div>                                                                                            
           })

           return (
        <div >
           <h1>BoekenLijst</h1>           
           <ul>{laadBoeken}</ul>                  
        </div>
    );

};

export default bookList;
