import React from 'react';



class bookForm extends React.Component {


   render(){

       return (
        <div>            
           <form className='ui form' style={ width= "200px"}>
               <div className='field'>
                   <label>Titel</label>
                   <input type="text" name="title" placeholder="Title van het boek.."></input>
               </div>

               <div className='field'>
                   <label>Auteur</label>
                   <input type="text" name="auteur" placeholder="Naam van auteur.."></input>
               </div>

               <div className='field'>
                   <label>Jaar</label>
                   <input type="text" name="jaar" placeholder="Jaar van uitgave.."></input>
               </div>

               <div className='field'>
                   <label>Prijs</label>
                   <input type="text" name="price" placeholder="Prijs van het boek..."></input>
               </div>

               <input className="ui button"type="button" value="Voeg toe"></input>

           </form>              
        </div>


    );
    }
};

export default bookForm;
