import React from 'react';



const book = props => {   

    function handleChecked(e) {
        let isChecked = e.target.value;

        console.log(isChecked);
      }

    return (
        <div className='ui divided items'> 
            <div className='class="item'>
            <input type="checkbox" onChange={e => handleChecked(e)} value={props.id}></input>               
                <div className='meta'>     
                    <h2 className="header" value={props.title}>{props.title}</h2> 
                    </div>                          
                            <div className='meta' >    
                        <p className= "cinema">{props.auteur}</p>
                            </div>
                        <div className="extra">
                            <p className='ui label'>{props.jaar}</p>
                            <p className='ui label'>{props.price}</p> 

                            <div className='ui red label' onClick={() => props.handleDelete(props.id)}>
                            <i className="trash alternate icon"></i>
                            </div>
                            </div>
                        </div>   
           </div>                      
    );
};

export default book;
