import React from 'react';



class bookForm extends React.Component {

    state = {
        title: '',
        auteur: '',
        jaar: '',
        price: ''
      }
    handleChange = (e) => {
        this.setState({
          [e.target.name]: e.target.value
        })
      }


   render(){

       return (
        <div>            
           <form className='ui form' style={{ width: "100%"}} onSubmit={ (e) => this.props.handleSubmit(e, this.state)}>

               <div className='field'>
                   <label>Titel</label>
                   <input onChange={this.handleChange} type="text" name="title" placeholder="Title van het boek.."></input>
               </div>

               <div className='field'>
                   <label>Auteur</label>
                   <input onChange={this.handleChange} type="text" name="auteur" placeholder="Naam van auteur.."></input>
               </div>

               <div className='field'>
                   <label>Jaar</label>
                   <input onChange={this.handleChange} type="number" name="jaar" placeholder="JJJJ" min="1950" max="2022"></input>
               </div>

               <div className='field'>
                   <label>Prijs</label>
                   <input onChange={this.handleChange} type="number" name="price" placeholder=",-" ></input>
               </div>

               <input className="ui button"type="submit" value="Voeg toe" ></input>

           </form>              
        </div>


    );
    }
};

export default bookForm;
