import React from "react";

import editableHoc from "./editableHoc";

class EditableText extends React.Component{
    constructor(props){
      super(props)
      console.log(props);
      this.state = {

        type: props.type||'text',
        value: props.value|| '',
        edit: false
      }
    }

    onClick = (state, props) => {
      this.setState({edit: state.edit!==true})
    }

    // onKeyUp = event => {
    //   if(event.key==='Escape') {
    //     this.setState((state) => ({edit:false, value: state.value}))
    //   } 
    // } 

    // onChange = (event) => {
    //   this.setState(() => ({value: event.target.value}))
    //   }

    // onFocus = event=>{
    //     const value = event.target.value
    //     event.target.value = ''
    //     event.target.value = value
    //     this.setState((state, props) => ({ backup: state.value})) }

    // onBlur = event => {
    //   this.setState({edit:false})
    // }    

    render() {

      return (

        this.state.edit===true&& 
        <div>
        <input 
          type={this.props.type}
          value={this.props.value}
          onFocus={this.props.onFocus}
          onChange={this.props.onChange}
          onBlur={this.props.onBlur}
          onKeyUp={this.props.onKeyUp}
        />
        <button onClick = {this.props.onClick }

        >Save</button>
        </div>
       ||
        <div onClick={this.props.onClick}>
          {this.state.value}
        </div>
      )
    }
  }


export default editableHoc(EditableText);
