import React from 'react';



function BookForm({onAddBook}){

    const [title, setTitle] = useState("");

    const handleChange = e => setTitle(e.target.value);


       return (
        <div>            
           <form className='ui form' style={{ width: "100%"}}>

               <div className='field'>
                   <label>Titel</label>
                   <input type="text" name="title" placeholder="Title van het boek.." onChange={handleChange}></input>
               </div>

               <div className='field'>
                   <label>Auteur</label>
                   <input type="text" name="auteur" placeholder="Naam van auteur.."></input>
               </div>

               <div className='field'>
                   <label>Jaar</label>
                   <input type="text" name="jaar" placeholder="Jaar van uitgave.."></input>
               </div>

               <div className='field'>
                   <label>Prijs</label>
                   <input type="text" name="price" placeholder="Prijs van het boek..."></input>
               </div>

               <input className="ui button"type="button" value="Voeg toe" onClick={() => onAddBook(title)}></input>

           </form>              
        </div>


    );

};

export default BookForm;
