import React, { useState, Component } from 'react'

export default (ChildComponent) => {
    class editableBase extends Component {
        constructor(props){
            super(props)
            // console.log(props)
            this.state = {

              type: props.type||'text',
              edit: false
            }
          }





        onClick = (state, props) => {
            this.setState({edit: this.state.edit!==true})
            console.log('base click')
          }

        onKeyUp = event => {
            if(event.key==='Escape') {
              this.setState((state, props) => ({edit:false, value: state.value}))
            } 
          } 


          // event => {
          //   // this.props.text = event.target.value
          //   // this.setState(() => ({value: event.target.value}))
          //   }

          onFocus = event => {
              const value = event.target.value
              event.target.value = ''
              event.target.value = value
              this.setState((state, props) => ({ backup: state.value})) }

          onBlur = () => {
            this.setState({edit:false})
          }    

        render() { 

          const Example = (props) => {
            // Title is bound to state within this component
            const [text, setText] = useState(this.props.text);

            const  onChange = e => {
              setText(e.target.value);


            if(this.state.edit===true ){
                return <div>
                <input 
                  type={this.state.type}
                  value={text}
                  autoFocus  
                  onFocus= {this.onFocus}
                  onChange = {onChange}
                  onKeyUp = {this.onKeyUp}
                  onBlur = {this.onBlur}
                  /> 
                <button onClick = {this.onClick }> Save </button>
                </div>
                }
              }
            }

            return <ChildComponent  
                    {...this.props }
                    onClick = {this.onClick}
                    value = {this.state.value}
            />           
        }
      }
      return editableBase;
};
