import React from 'react';
import BookList from "./bookList"
import BookForm from "./bookForm"





class App extends React.Component {

    state = {
        books: [{
            "Id": 1,
            "title": "Harry potter",
            "auteur": "J.K. Rowling",
            "jaar": 2010,
            "price": "45,-"
        },{
            "Id": 2,
            "title": "Vermist",
            "auteur": "Joe Cuba",
            "jaar": 2003,
            "price": "20,-"
        },{
            "Id": 3,
            "title": "Roadtrip",
            "auteur": "Dennis Hopper",
            "jaar": 2008,
            "price": "15,-"
        },{
            "Id": 4,
            "title": "Ugly love",
            "auteur": "Janis Joplin",
            "jaar": 2016,
            "price": "18,-"
        },{
            "Id": 5,
            "title": "Rich dad, poor dad",
            "auteur": "Harry Mulish",
            "jaar": 2022,
            "price": "25,-"
        }],

      }



    handleSubmit = (e, obj) => {
        e.preventDefault()
        const submitted = [...this.state.books, obj]
        this.setState({
          books: submitted
        }, () => console.log(this.state))
      }

    handleRemoveClick = (obj) => {
        console.log('REMOVE FUNCTION', obj)
        const removedItemFromBookshelf = this.state.books.filter(books => {
          return books.id !== obj.id
        })
        this.setState({
          book: removedItemFromBookshelf
        })
      }





    render () {

          return <div className='ui placeholder segment'>
                    <div className= 'ui two column very relaxed stackable grid' >
                        <div className='middle aligned column'>
                     <BookList books={this.state.books} handleRemoveClick={this.handleRemoveClick}   /> 
                     </div>
                     <div className="middle aligned column">
                     <BookForm handleSubmit={this.handleSubmit} />

                     </div>
                    </div>
                 </div>


    }
}

export default App;
